        try {
            String originalValue = value.toString()
            if (originalValue.length() > 5) {
                String prefix = originalValue.substring(0, 5)
                String suffix = originalValue.substring(5)
                byte[] encryptedBytes = cipher.doFinal(suffix.getBytes("UTF-8"))
