    if (value != null && value.toString().trim() != "") {
        try {
            String originalValue = value.toString()
            if (originalValue.length() > 4) {
                String prefix = originalValue.substring(0, 4)
                String suffix = originalValue.substring(4)
                byte[] encryptedBytes = cipher.doFinal(suffix.getBytes("UTF-8"))
